import streamlit as st
list_options1 = ["3☆", "4☆", "5☆"]
list_options2 = ["1 giường", "2 giường", "giường kép", "giường đôi"]
list_options3 = ["ăn sáng(buffet)", "ăn trưa", "ăn tối", "combo(sáng, trưa, tối)", "không dùng"]
image1 = "data:image/jpeg;base64,"
image2 = "data:image/jpeg;base64,"
image3 = "data:image/jpeg;base64,"
col1, col2, col3 = st.columns(3)
with st.form("khachsan"):
    st.title("Đặt phòng khách sạn")
    with col1:
        b1 = st.button("xem phòng 3☆")
    with col2:
        b2 = st.button("xem phòng 4☆")
    with col3:
        b3 = st.button("xem phòng 5☆")
    if b1:
        with st.expander("Mẫu phòng 3☆"):
            st.image(image1)
    if b2:
        with st.expander("Mẫu phòng 4☆"):
            st.image(image2)
    if b3:
        with st.expander("Mẫu phòng 5☆"):
            st.image(image3)
    phong = st.selectbox("phòng", list_options1)
    giuong = st.selectbox("giường", list_options2)
    an = st.selectbox("bữa ăn", list_options3)
    submitted = st.form_submit_button("submit")
    if submitted:
        st.write("phòng bạn đặt là")
        if len(phong) == 0:
            st.write("Bạn chưa chọn phòng")
        else:
            st.write(phong)
        st.write("dịch vụ giường:")
        if len(giuong) == 0:
            st.write("bạn chưa chọn giường")
        else:
            st.write(giuong)
        st.write("dịch vụ bữa ăn bạn chọn là")
        if len(an) == 0:
            st.write("Bạn chưa chọn món dịch vụ ăn")
        else:
            st.write(an)